import React, { useState, useEffect } from 'react';
import axios from 'axios';


export default () => {
    const [posts, setPosts] = useState({});

    const fetchPosts = async () =>{
        const res = await axios.get('http://localhost:4000/posts');
        setPosts(res.data);
    };
    // running this function only once.
    useEffect(() => {
        fetchPosts();
    }, []);

    console.log(posts);
    return <div />;

};
